import React, { useState, useEffect } from "react";
import "./App.css";
import QuizView from "./components/QuizView";
import ScoreView from "./components/ScoreView";

const API_KEY = "YOUR_API_KEY"; // Replace with your API key

function App() {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [isQuizOver, setIsQuizOver] = useState(false);
  const [score, setScore] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await fetch(`https://quizapi.io/api/v1/questions?apiKey=${API_KEY}&category=code&limit=10`);
        const data = await response.json();

        if (Array.isArray(data)) {
          const formattedQuestions = data.map(q => ({
            question: q.question,
            correctAnswer: Object.values(q.answers).find(answer => answer.isCorrect)?.text,
            answers: Object.entries(q.answers).map(([key, answer]) => ({
              text: answer.text,
              isCorrect: answer.isCorrect,
            })),
          }));
          setQuestions(formattedQuestions);
        }
      } catch (error) {
        console.error("Error fetching questions:", error);
      }
    };

    fetchQuestions();
  }, []);

  const handleAnswerClick = (isCorrect, answer) => {
    if (isCorrect) setScore(score + 1);
    setUserAnswers([...userAnswers, { answer, isCorrect }]);

    const next = currentQuestion + 1;
    if (next < questions.length) setCurrentQuestion(next);
    else setIsQuizOver(true);
  };

  const handleResetClick = () => {
    setScore(0);
    setCurrentQuestion(0);
    setIsQuizOver(false);
    setUserAnswers([]);
  };

  return (
    <div className="App">
      {isQuizOver ? (
        <ScoreView 
          handleResetClick={handleResetClick} 
          score={score} 
          totalQuestions={questions.length} 
          userAnswers={userAnswers} 
        />
      ) : (
        questions.length > 0 && (
          <QuizView
            questions={questions}
            currentQuestion={currentQuestion}
            handleAnswerClick={handleAnswerClick}
          />
        )
      )}
    </div>
  );
}

export default App;
